/**
 *  Extract all image URLs from HTML string
 */
- (NSArray *)getImagesFromHTML:(NSString *)html
{
    NSMutableArray *images = [[NSMutableArray alloc] init];
    
    NSRegularExpression *regex = [NSRegularExpression regularExpressionWithPattern: @"src=\"(.*?)\""//@"<img[^>]*>"
                                                                           options: NSRegularExpressionCaseInsensitive
                                                                             error: nil];
    
    for (NSTextCheckingResult *result in [regex matchesInString: html
                                                        options: 0
                                                          range: NSMakeRange(0, [html length])]) {
        
        NSString *match = [regex replacementStringForResult: result
                                                   inString: html
                                                     offset: 0
                                                   template: @"$0"];

        match = [match stringByReplacingOccurrencesOfString:@"\"" withString:@""];
        match = [match stringByReplacingOccurrencesOfString:@"src=" withString:@""];
        
        if (![match isEqualToString:@""]) {
            [images addObject: match];
        }
    }
    return images;
}




- (IBAction)extract:(id)sender
{
    NSString *html = _textView.text;
    NSArray *images = [self getImagesFromHTML: html];
    
    NSLog(@"Found %i images: %@", [images count], images);
    
    [_btnExtract setTitle:@"All done! Check console." forState:UIControlStateNormal];
    [_btnExtract setTitle:@"All done! Check console." forState:UIControlStateHighlighted];
    double delayInSeconds = 2.0;
    dispatch_time_t popTime = dispatch_time(DISPATCH_TIME_NOW, (int64_t)(delayInSeconds * NSEC_PER_SEC));
    dispatch_after(popTime, dispatch_get_main_queue(), ^(void){
        [_btnExtract setTitle:@"Extract images from HTML" forState:UIControlStateNormal];
        [_btnExtract setTitle:@"Extract images from HTML" forState:UIControlStateHighlighted];
    });
    
}




